
var first_names = ["Peter","John","Anna","Neil","Chris","Adam","Robert","Eva"]
var last_names = ["Adams","Jones","Smith","White","Denby","Johnson","Clegg"]

/**
 * Returns a random integer between min (inclusive) and max (inclusive)
 * Using Math.round() will give you a non-uniform distribution!
 */
function getRandomInt(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
}

var personnel = function() {
    
    var first_name = first_names[getRandomInt(0, first_names.length - 1)];
    var last_name = last_names[getRandomInt(0, last_names.length - 1)];
    
    return {
        name: first_name + ' ' + last_name,
        email: first_name.substring(0,1).toLowerCase() + last_name.toLowerCase() + '@example.com',
        phone: '+1 310 343 ' + getRandomInt(1000,9999)
    }
} 


module.exports.personnel = personnel;
